import React, {Component} from 'react';
import {Card} from 'react-bootstrap';

export class AdviceCard extends React.Component {

    constructor(props) {
      super(props);

        this.state = {
          cardName: props.name,
          cardDesc: props.desc,
          cardImg: props.img,
          cardAlt: props.alt
        };

        let name = "hi";
        let desc = props.desc;
        let img = props.img;
        let alt = props.alt;
    }

    render() {
        return (
            <Card className="cream-background centered" id="advice-card"
            onMouseEnter={() => {this.onMouseEnter();}} onMouseOut={() => {this.onMouseOut();}}>
                <img src={this.props.img} alt={this.props.alt} className="individ-step"/>
                <h3 id="adviceTitle">{this.props.name}</h3>
            </Card>
        )
    }

    onMouseEnter= () => {
        this.setState({
          cardContent: "advice wow"
        })
    }

    onMouseOut= () => {
        this.setState({
          cardContent: "hello i am a card"
        })
    }
}
