import React, {Component} from 'react';
import * as d3 from 'd3';


export class Breakdown extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: this.props.gardenData
        }
    }

    drawChart() {
        const margin = ({ top: 20, right: 60, bottom: 40, left: 40 });

        const height = 500;

        const width = 700;

        const color = d3.scaleOrdinal()
            .domain(this.state.data.map(d => d.PlantName))
            .range(["#F4EBC2", "#D8C469", "#feffa6", "#E89B55"]);


        const max_CO2 = d3.max(this.state.data, d => d.TotalCO2);

        const x_scale = d3.scaleLinear()
            .domain([0, max_CO2])
            .range([margin.left + 60,width - margin.right]);

        const y_scale = d3.scaleBand()
            .domain(this.state.data.map(d => d.PlantName))
            .range([height - margin.bottom, margin.top])
            .padding(0.1);

        const xAxis = g => g
            .attr("transform", `translate(0,200)`)
            .call(d3.axisBottom(x_scale));

        const yAxis = g => g
            .attr("transform", `translate(${margin.left},0)`)
            .call(d3.axisLeft().scale(y_scale));

        const chart = d3.select("body").append("svg")
            .attr("width", width)
            .attr("height", height)
            .style("background-color", "#679967");

        chart.append('g')
            .call(xAxis)
            .attr("transform", "translate(0, 480)")
            .style("color", "white");

        chart.append('g')
            .call(yAxis)
            .attr("transform", "translate(100,0)")
            .style("color", "white");

        chart.append("g")
        .selectAll("rect")
        .data(this.state.data)
        .join("rect")
            .attr("fill", (d) => color(d.PlantName))
            .attr("x", x_scale(0))
            .attr("y", (d, i) => y_scale(d.PlantName) + 10)
            .attr("width", d => x_scale(d.TotalCO2) - x_scale(0))
            .attr("height", 25); // y_scale.bandwidth()


    }

    componentDidMount() {
        this.drawChart();
    }

    render() {
        return (
          <section className="green-background">
            <div className="container">
              <div className="centered">
                <h3 className="centered emissions-header">Plantastic! Let's take a look at the emissions that were saved by each vegetable...</h3>
              </div>
            </div>
          </section>
        )
    }
}
