import React, {Component, useState} from 'react';
import {Container, Row, Col, Button} from 'react-bootstrap';

export class SinglePlant extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: {}
        };
    }

    componentDidMount() {
        fetch("https://verdancy.capstone.ischool.uw.edu/v1/plants/1")
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result);
                    console.log(result.items);
                    this.setState({
                        isLoaded: true,
                        items: result
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }
    render() {
        const {error, isLoaded, items} = this.state;
        console.log(items);
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return(
                <div>
                    <Container>
                        <Row>
                            <Col>
                                <h1>{items.PlantName}</h1>
                            </Col>
                            <Col>
                                <Button variant="primary" size="lg">+ add to garden</Button>
                            </Col>
                        </Row>
                        <Row>
                            <p>{items.CO2PerUnit} g CO2e saved per tomato | Gardening Difficulty: {items.Difficulty}</p>
                        </Row>
                    </Container>

                    <Container>
                        <Row>
                            <Col lg={8}>
                                <p>{items.Descr}</p>
                                <h3>Plant Care</h3>
                                <hr/>
                                <Container>
                                    <Row>
                                        <p>Watering: {items.Watering} </p>
                                    </Row>
                                    <Row>
                                        <p>Sunlight: {items.Sun} </p>
                                    </Row>
                                    <Row>
                                        <p>Soil: {items.Soil} </p>
                                    </Row>
                                    <Row>
                                        <p>Timing: {items.WhenToPlant} </p>
                                    </Row>
                                </Container>
                            </Col>
                            <Col lg={4}>
                                <img src={items.ImageLink} alt="pic of plant" className="img-100" />

                            </Col>
                        </Row>
                    </Container>
                </div>
            )
        }
    }
}
