import React, {Component, useState} from 'react';
import {Navbar, Nav, Button} from 'react-bootstrap';
import {Row, Container} from "react-bootstrap";

export class EmissionsTitle extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            toggleDisplay: "hidden"
        };
    }

    render() {
        return (
          <section className="green-background">
            <div className="container">
              <Row className="row">
                <div className="left-stuff">
                    <div className="page-header">
                      <h2>my emissions</h2>
                    </div>
                    <h1 id="emissions-num" className="page-content">{this.props.emissions}<span className="medium-text"> g <span className="darkgreen-text">CO2e saved</span></span></h1>
                </div>
                <img src="https://raw.githubusercontent.com/annazhoufast/verdancy-client/main/src/imgs/emissions/emissions.png?token=AKMRZNURTLNIKKYHSJ7A2XTAT374G"
                alt="woman holding up a globe" id="emissions-img"/>
              </Row>
              <Button className="cream-button" showDisplay={() => {this.showDisplay();}}>
                  <span>Learn more</span>
              </Button>
              <div className={this.props.toggleDisplay}>
                  <p>
                      I am trying to change the style of the pill by passing in the name of the class that has the respective style. I am new to React so maybe I am not doing this the right way. Thanks!
                  </p>
                  <Button className="cream-button" hideDisplay={() => {this.hideDisplay();}}>
                      <span>OK, got it!</span>
                  </Button>
              </div>
            </div>
          </section>
        )
    }

    showDisplay= () => {
        this.setState({
          toggleDisplay: "display-on"
        })
        {console.log({toggleDisplay})}
    }

    hideDisplay= () => {
        this.setState({
          toggleDisplay: "hidden"
        })
    }

}
