import React, {Component, useState} from 'react';
import {Row, Container} from "react-bootstrap";

export class EmissionsTitle extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            totalEm: [],
            auth: localStorage.getItem("Authorization")
        }
    }

    componentDidMount() {
        fetch("https://verdancy.capstone.ischool.uw.edu/v1/emissions/", {
            method: 'GET',
            headers: new Headers({
                'Authorization': this.state.auth
            })
        })
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        totalEm: result
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        console.log(this.state.totalEm);
        return (
          <section className="green-background">
            <div className="container">
              <Row>
                <div>
                    <div className="content-header">
                      <h2>my emissions</h2>
                    </div>
                    <h1 id="emissions-num">30,750 <span className="darkgreen-text medium-text">g CO2e Saved</span></h1>
                </div>
                {/* image will go here */}
              </Row>
            </div>
          </section>
        )
    }
}
