import React, {Component} from 'react';
import {AdviceCard} from './AdviceCard.js';
import {Row, Container} from 'react-bootstrap';
import {IndividualTeam} from './IndividualTeam.js';

export class AdviceCardGroup extends React.Component {
    render() {

        {/*
        // create the cards here
        let cards = [];
        for (let i = 0; i < 6; i++) {
            cards.push(<AdviceCard/>)
        }
        */}

        {/*
        var advice = require("../../data/advice.json");
        let cards = [];
        for (let i = 0; i < advice.length; i++) {
            cards.push(
                <AdviceCard front={advice[i].front}
                                back={advice[i].back}
                                frontAlt={advice[i].frontAlt}
                                backAlt={advice[i].backAlt} />
            );
        }
        */}

        var teamData = require("../../data/team.json");
        let ppl = [];
        for (let i = 0; i < teamData.length; i++) {
            ppl.push(<IndividualTeam pfp={teamData[i].Img}
                                        alt={teamData[i].Alt}
                                        name={teamData[i].Name}
                                        role={teamData[i].Role} />
                    );
        }

        return(
          <section className="centered" id="advice">
            <div className="container centered">
              <div className="centered">
                <Row id="advice-row" className="centered">
                  {ppl}
                </Row>
              </div>
            </div>
          </section>
        )
    }
}
