import React, {Component} from 'react';
import {Container, Row, Col, Button} from 'react-bootstrap';

export class SinglePlant extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

    componentDidMount() {
        fetch("https://verdancy.capstone.ischool.uw.edu/v1/plants/1")
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result);
                    console.log(result.items);
                    this.setState({
                        isLoaded: true,
                        items: result
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        return(
            <div>
                <Container>
                    <Row>
                        <Col>
                            <h1>Tomato</h1>
                        </Col>
                        <Col>
                            <Button variant="primary" size="lg">+ add to garden</Button>
                        </Col>
                    </Row>
                    <Row>
                        <p>132 g CO2e saved per tomato | Gardening Difficulty: Hard</p>
                    </Row>
                </Container>

                <Container>

                </Container>
            </div>
        )
    }
}
