import React, {Component, useState} from 'react';
import {Result} from './Result';
import {Row, CardDeck} from 'react-bootstrap';
import Form from './Form';

export class ResultGroup extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: this.props.plants,
            plants: [],
            isLoaded: false,
            searchTerm: ''
        };
    }

    editSearchTerm = (e) => {
        this.setState({searchTerm: e.target.value});
        return this.state.plants.filter(cur => cur.PlantName.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    }

    dynamicSearch = () => {
        return this.state.plants.filter(cur => cur.PlantName.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
    }

    componentDidMount() {
        const response = fetch("https://verdancy.capstone.ischool.uw.edu/v1/plants")
            .then(res => res.json())
            .then((result) => {
                // console.log(result);
                this.setState({plants: result, isLoaded: true});
            });
    }

    render() {
        // if (!this.state.isLoaded) {
        //     return (
        //         <div>Loading...</div>
        //     )
        // console.log(this.state.searchTerm);
        // } else {

        const stuff = this.state.plants.map(item => (
            <Result pName={item.PlantName} pSName={item.PlantScientificName}
                difficulty={item.Difficulty} image={item.ImageLink} id={item.PlantID} />

        ));
        // console.log(stuff)
        stuff.filter(d => d.PlantName.includes(this.state.searchTerm.toLowerCase()))

            return (
                <div className="container">
                    <div style = {{textAlign: 'center', paddingTop: '30vh'}}>
                        <input type= 'text' value = {this.state.searchTerm} onChange = {this.editSearchTerm} placeholder = 'Search for a name!'/>
                        <br></br>
                        {/* <h3>These are the important names:</h3>
                        <NamesContainer names = {this.dynamicSearch()}/> */}
                    </div>
                  <div id="results">
                      <Row className="db-row">
                          {
                            stuff
                            // this.state.plants.map(item => (
                            //     <Result pName={item.PlantName} pSName={item.PlantScientificName}
                            //         difficulty={item.Difficulty} image={item.ImageLink} id={item.PlantID} />

                            // ))
                           // .filter(d => d.PlantName.includes(this.state.searchMessage))
                            // this.state.plants
                          }
                      </Row>
                  </div>
                </div>
            )
                    // }
    }
}
