import React, {Component, useState} from 'react';
import {Card, Button, Row, Col, Modal, Form, Container} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import NumericInput from 'react-numeric-input';


// export class Plant extends React.Component {

// function updateQuant(quant) {
//     this.setState({quant});
// }

// function Plant(props) {
class Plant extends Component {
    // const [lgShow, setLgShow] = useState(false);
    // const quant = useState(0);
    // console.log(quant);
    // func updateQuant() = quant => this.setState({ quant });
    // updateQuant = quant => this.setState({quant});
    constructor(props) {
        super(props);
        this.state = {
            lgShow: false,
            setLgShow: false,
            quant: 0
        }
    }

    updateQuant = (curQuant) => {
        this.setState({quant: curQuant})
    }



    render() {
    return (
        <Col lg={6} className="harvest-card">
            <Container className="outline">
                <Row>
                    <Col lg={5}>
                        <img src="https://raw.githubusercontent.com/annazhoufast/plantastic/main/data/imgs/tomatoe.png?token=ALLXA25BKVLL7A7AEQS3W3LASHPF6" 
                            alt="drawing of plant" className="garden-img" />
                    </Col>
                    <Col lg={7} className="harvest">
                        <h4>
                            <b>{this.props.pName}</b>
                        </h4>
                        <p>Harvest Total</p>
                        <p>{this.props.quantity}</p>
                        <p>Carbon Emissions Saved</p>
                        <p>{props.totCarbon} g</p>
                        <Button onClick={() => this.setState({setLgShow: true})} block className="green-btn">Harvest!</Button>
                        <Modal
                            size="lg"
                            show={this.state.lgShow}
                            // onHide={() => setLgShow(false)}
                            onHide={() => this.setState({setLgShow: false})}
                            aria-labelledby="example-modal-sizes-title-lg"
                        >
                            <Modal.Header closeButton></Modal.Header>
                            <Modal.Body>
                                <Container>
                                    <Row>
                                        <Col>
                                            <p>How much {this.props.pName} did you harvest?</p>
                                        </Col>
                                        <Col>
                                            <NumericInput min={0} value={quant} onChange={this.updateQuant(quant)} />
                                            <p>{quant}</p>
                                        </Col>
                                    </Row>
                                    <Form>
                                        {['checkbox'].map((type) => (
                                            <div key={`default-`} className="mb-3">
                                                <Form.Check
                                                    type={type}
                                                    id={`default-`}
                                                    label={`add to your emissions total?`}
                                                />
                                            </div>
                                        ))}
                                    </Form>
                                    <Button className="green-btn" size="lg" onClick={() => this.setState({setLgShow: false})}>Confirm</Button>
                                </Container>
                            </Modal.Body>
                        </Modal>
                    </Col>
                </Row>
            </Container>
        </Col>
    )
                                        }
}
export default Plant;
