import React, {Component} from 'react';
import {Card} from 'react-bootstrap';

export class AdviceCard extends React.Component {

  render() {

    const front = this.props.front;
    const back = this.props.front;

      return (
          <Card className="cream-background centered" id="advice-card"
          onMouseEnter={() => {this.onMouseEnter();}} onMouseOut={() => {this.onMouseOut();}}>
              <img src={this.state.cardImg} alt={this.props.frontAlt}/>
          </Card>
      )
  }

  onMouseEnter= () => {
      this.setState({
        cardImg: {front}
      })
  }

  onMouseOut= () => {
      this.setState({
        cardImg: {back}
      })
  }
}
