import React, {Component, useState} from 'react';
import {Card, Button, Row, Col, Modal} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";

export class Plant extends React.Component {
    constructor() {
        this.state = {
            lgShow: false,
            setLgShow: false
        }
    }
    render() {
        const [lgShow, setLgShow] = useState(false);
        return (
            <Col lg={6} className="harvest-card">
                <Card>
                    <Row>
                        <Col lg={5}>
                            {/* <img src={this.props.img} alt="drawing of plant" /> */}
                            <Card.Img src="https://raw.githubusercontent.com/annazhoufast/plantastic/main/data/imgs/tomatoe.png?token=ALLXA25BKVLL7A7AEQS3W3LASHPF6"
                                alt="drawing of plant"
                                className="garden-img" />
                        </Col>
                        <Col lg={7}>
                            <Card.Body className="harvest">
                                <Card.Title>
                                    {/* {this.props.pName} */}
                                    <h4>
                                        <b>Tomatoes</b>
                                    </h4>
                                </Card.Title>
                                <Card.Text>
                                    <p>Harvest Total</p>
                                    <p>3</p>
                                    <p>Carbon Emissions Saved</p>
                                    <p>357 g</p>
                                </Card.Text>
                                <Button onClick={() => setLgShow(true)} block>Harvest!</Button>
                                <Modal
                                    size="lg"
                                    show={lgShow}
                                    onHide={() => setLgShow(false)}
                                    aria-labelledby="example-modal-sizes-title-lg"
                                >
                                    <Modal.Header closeButton>
                                    <Modal.Title id="example-modal-sizes-title-lg">
                                        Large Modal
                                    </Modal.Title>
                                    </Modal.Header>
                                    <Modal.Body>...</Modal.Body>
                                </Modal>
                            </Card.Body>

                        </Col>
                    </Row>
                </Card>
            </Col>
        )
    }
}
