import React, {Component, useState} from 'react';
import {Navbar, Nav, Button} from 'react-bootstrap';
import {Row, Container} from "react-bootstrap";

export class EmissionsTitle extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            toggleDisplay: "hidden"
        };
    }

    render() {
        return (
          <section className="green-background">
            <div className="container">
              <Row className="row">
                <div className="left-stuff">
                    <div className="page-header">
                      <h2>my emissions</h2>
                    </div>
                    <h1 id="emissions-num" className="page-content">{this.props.emissions}<span className="medium-text"> g <span className="darkgreen-text">CO2e saved</span></span></h1>
                </div>
                <img src="https://raw.githubusercontent.com/annazhoufast/verdancy-client/main/src/imgs/emissions/emissions.png?token=AKMRZNURTLNIKKYHSJ7A2XTAT374G"
                alt="woman holding up a globe" id="emissions-img"/>
              </Row>
              <Button className="cream-button">
                  <span>Learn more</span>
                  showDisplay={() => {this.showDisplay();}}
              </Button>
              <div>
                  <p>
                      Join us on Facebook and/or Discord to keep up to date with all current club events, updates, and announcements! Our Linktree contains all of our media channels, as well as other important information on how to play with our club.
                      Despite the challenges of COVID-19, we are still hoping to continue building a strong community of people who love badminton! We are planning ways to to stay connected and meet new badminton friends in a safe manner, and hope to see you join us along for the ride!
                  </p>
                  <Button className="cream-button">
                      <span>OK, got it!</span>
                  </Button>
              </div>
            </div>
          </section>
        )
    }

    showDisplay= () => {
        this.setState({
          toggleDisplay: "display-on"
        })
    }

    hideDisplay= () => {
        this.setState({
          toggleDisplay: "hidden"
        })
    }

}
