import React, {Component, useState} from 'react';
import {Result} from './Result';
import {Row, CardDeck} from 'react-bootstrap';
import Form from './Form';

export class ResultGroup extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: this.props.plants,
            plants: [],
            isLoaded: false,
            searchMessage: ''
        };
    }

    componentDidMount() {
        const response = fetch("https://verdancy.capstone.ischool.uw.edu/v1/plants")
            .then(res => res.json())
            .then((result) => {
                console.log(result);
                this.setState({isLoaded: true});
                result.map(r => (
                    <Result pName={r.PlantName} pSName={r.PlantScientificName}
                            difficulty={r.Difficulty} image={r.ImageLink} id={r.PlantID} />
                ));
            });
    }

    render() {
        // if (!this.state.isLoaded) {
